import React from 'react';
import './AppStore.css';

import { makeStyles } from '@material-ui/core/styles';
import Install from '@material-ui/core/Button';

import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';

import RestoreIcon from '@material-ui/icons/Restore';
import FavoriteIcon from '@material-ui/icons/Favorite';
import SearchIcon from '@material-ui/icons/Search';
import TodayIcon from '@material-ui/icons/Today';

import Fab from '@material-ui/core/Fab';


import Ferp from './resources/Calendar - Blank.png'

const useStyles = makeStyles(theme => ({
    button: { 
        margin: theme.spacing(0.1),
    },
    input: {
        display: 'none',
    },
    root: {
        maxWidth: 500,
    },
    fab: {
        margin: theme.spacing(0,1,0,0),
    }
}));

//Simple Info View on Top of AppStore Page
function AppStoreScreen() {

    const classes = useStyles();
    const [value, setValue] = React.useState(0);

    return(
        <section className="background_app_store">
            <NaviInfo></NaviInfo>
            <SimpleInfo></SimpleInfo>
            {/* line for distribute */}
            <hr
                style={{
                    color: "red",
                    backgroundColor: "red",
                    height: 1
                }}
            />
            <DescriptionInfo></DescriptionInfo>
            <BottomNavigation
                value={value}
                onChange={(event, newValue) => {
                    setValue(newValue);
                }}
                showLabels
                className={classes.root}
                >
                <BottomNavigationAction label="Today" icon={<TodayIcon />} />
                <BottomNavigationAction label="Favorites" icon={<FavoriteIcon />} />
                <BottomNavigationAction label="Search" icon={<SearchIcon />} />
            </BottomNavigation>
        </section>
    );
}

const NaviInfo = () => {
    return(<div></div>);
}

const SimpleInfo = () => {

    const classes = useStyles();

    return(
        <article className="simple_info">
            <div className="Logo"><img src={Ferp} alt="logo"></img></div>
            <div className="AppName">
                <div className="AppName_Inside">
                    <h4>Ferp Period & Ovulation Tracker</h4>
                </div>
            </div>
            <div className="CompanyName">
                <div className="CompanyName_Inside">
                    <p>UCI</p>
                </div>
            </div>
            <div className="InstallButton">
                <Install variant="contained" color = "primary" size="small"
                    className={classes.button}>Install
                 </Install>
            </div>
            <div className="BlankNull"></div>
            <div className="OtherButton">
                <Fab color="primary" aria-label="Other" className={classes.fab} size="small">
                    ...
                </Fab>
            </div>
            <div className="Rating">
                <h4>4.7</h4>
                <h6>36.9K Ratings</h6>
            </div>
            <div className="Ranking">
                <h4>#2</h4>
                <h6>Reference</h6>
            </div>
            <div className="Age">
                <h4>17+</h4>
                <h6>Age</h6>
            </div>
        </article>
    )
}

const DescriptionInfo = () => {
    return(
        <div></div>
    )
}

export default AppStoreScreen
