import React from 'react';

const list = [
  {
    id: 'a',
    firstname: 'Robin',
    lastname: 'Wieruch',
    year: 1988,
  },
  {
    id: 'b',
    firstname: 'Dave',
    lastname: 'Davidds',
    year: 1990,
  },
];

const List = () => (
    <ul>
      {list.map(item => {
        const ref = createRef();

        const handleClick = () =>
          ref.current.scrollIntoView({
            behavior: 'smooth',
            block: 'start',
          });

        return (
          <li
            key={item.id}
            ref={ref}
            style={{ height: '250px', border: '1px solid black' }}
          >
            <div>{item.id}</div>
            <div>{item.firstname}</div>
            <div>{item.lastname}</div>
            <div>{item.year}</div>
            <button type="button" onClick={handleClick}>
              Scroll Into View
            </button>
          </li>
        );
      })}
    </ul>
  );


export default List;
